import React from "react";
import SearchBar from './SearchBar';
import MovieList from './MovieList';

//class base component filmlerin sayısı değişken olacagı ıcın bılgıler state ıcıne gomulur bu yuzden class base kullanıyoruz
class App extends React.Component{

    state={
            movies : [
                {
                    "id":1,
                    "name": "test",
                    "rating": 8.3,
                    "overview": "aaaa",
                    "imageURL": "https://upload.wikimedia.org/wikipedia/tr/9/94/The_Imitation_Game_-_afi%C5%9F.jpg"

                },
                {
                    "id":2,
                    "name": "test2",
                    "rating": 8.3,
                    "overview": "aaaa2",
                    "imageURL": "https://upload.wikimedia.org/wikipedia/tr/9/94/The_Imitation_Game_-_afi%C5%9F.jpg"
                },
                {
                    "id":3,
                    "name": "test3",
                    "rating": 8.3,
                    "overview": "aaaa3",
                    "imageURL": "https://upload.wikimedia.org/wikipedia/tr/9/94/The_Imitation_Game_-_afi%C5%9F.jpg"
                },
                {
                    "id":4,
                    "name": "test",
                    "rating": 8.3,
                    "overview": "aaaa",
                    "imageURL": "https://upload.wikimedia.org/wikipedia/tr/9/94/The_Imitation_Game_-_afi%C5%9F.jpg"

                },
                {
                    "id":5,
                    "name": "test5",
                    "rating": 8.3,
                    "overview": "aaaa2",
                    "imageURL": "https://upload.wikimedia.org/wikipedia/tr/9/94/The_Imitation_Game_-_afi%C5%9F.jpg"
                },
                {
                    "id":6,
                    "name": "test6",
                    "rating": 8.3,
                    "overview": "aaaa3",
                    "imageURL": "https://upload.wikimedia.org/wikipedia/tr/9/94/The_Imitation_Game_-_afi%C5%9F.jpg"
                }
            ],

            searchQuery: ""
    }

    //app.js parent comp.dan movielist child comp.a propslarla delete işlemi tasınır
    //film silme işlemi için fonksiyon stateteki listeyi al yine ama silinenin id siyle aynı olmayanları
    //anlamına geliyor
    deleteMovie = (movie)=>{
        const newMovieList=this.state.movies.filter(
            m => m.id !== movie.id
        );

        //elimizde ilk durumda film bilgisi olmasaydı bu şablon kullanılırdı 
        //this.setState({
        //    movies : newMovieList
        //})

        //elimizde film bilgisi oldugu ıcın bu sekılde yazıyoruz
        this.setState(state =>({
            movies: newMovieList
        }))
    }
    
    //arama input una her yazı yazıldıgında searchQuery i o yazılanla update ediyoruz
    searchMovie=(event)=>{
        //console.log(event.target.value)
        this.setState({searchQuery: event.target.value})
    }

    render(){

        //indexof ile yazılan kelimeyi filmlerin name kısmı ıcınde arıyor bulamazsa indexof -1 döner o yuzden
        //-1 dönmediklerini yanı bulduklarını döndür diyoruz
        let filteredMovies = this.state.movies.filter(
            (movie)=>{
                return movie.name.indexOf(this.state.searchQuery) !== -1
            }
        )

        return(
            <div className="container">
                <div className="row">
                    <div className="col-lg-12">
                        <SearchBar
                            searchMovieProp={this.searchMovie}
                        />
                    </div>
                </div>

                <MovieList
                    movie={filteredMovies}
                    deleteMovieProp={this.deleteMovie}
                />
            </div>
        )
    }
}

//functional component
//const App=()=>{
//    return (
//        <h1>My Movies</h1>
//    )
//}
export default App;
