import React from "react";
import SearchBar from './SearchBar';
import MovieList from './MovieList';

//class base component filmlerin sayısı değişken olacagı ıcın bılgıler state ıcıne gomulur bu yuzden class base kullanıyoruz
class App extends React.Component{

    state={
            movies : [
                {
                    "id":1,
                    "name": "test",
                    "rating": 8.3,
                    "overview": "aaaa",
                    "imageURL": "https://upload.wikimedia.org/wikipedia/tr/9/94/The_Imitation_Game_-_afi%C5%9F.jpg"

                },
                {
                    "id":2,
                    "name": "test2",
                    "rating": 8.3,
                    "overview": "aaaa2",
                    "imageURL": "https://upload.wikimedia.org/wikipedia/tr/9/94/The_Imitation_Game_-_afi%C5%9F.jpg"
                },
                {
                    "id":3,
                    "name": "test3",
                    "rating": 8.3,
                    "overview": "aaaa3",
                    "imageURL": "https://upload.wikimedia.org/wikipedia/tr/9/94/The_Imitation_Game_-_afi%C5%9F.jpg"
                }
            ]
    }


    //film silme işlemi için fonksiyon stateteki listeyi al yine ama silinenin id siyle aynı olmayanları
    //anlamına geliyor
    deleteMovie = (movie)=>{
        const MovieList=this.state.movies.filter(
            m => m.id !== movie.id
        );
    }

    render(){
        return(
            <div className="container">
                <div className="row">
                    <div className="col-lg-12">
                        <SearchBar/>
                    </div>
                </div>

                <MovieList
                movie={this.state.movies}/>
            </div>
        )
    }
}

//functional component
//const App=()=>{
//    return (
//        <h1>My Movies</h1>
//    )
//}
export default App;
